import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import MediaService from './../../../service/media.service'

class Following extends Component {

    constructor(props) {
        super(props)
        this.state = {
            tv: [],
            movie: []
        }
        this.MediaService = new MediaService()
    }

    initFollowingLists = () => { }

    componentDidMount = () => {
        const tvArr = [], movieArr = []
        this.props.user.followingList.forEach(elm => elm.type === 'tv' ? tvArr.push(elm) : movieArr.push(elm))

        this.setState({ tv: tvArr, movie: movieArr })
        // this.MediaService.getAll('de las flores')
        //     .then(response => console.log(response.data.results[0]))
        //     .catch(err => console.log(err))
    }

    render() {
        return (
            <Container fluid>
                <h2>Series que sigues</h2>

                <h2>Pel√≠culas que sigues</h2>
            </Container>

        )
    }

}

export default Following
