import React, { Component } from 'react'

import MediaService from './../../../../service/media.service'
import MeetingService from './../../../../service/meeting.service'

import './CreateMeetingForm.css'

import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

class CreateMeetingForm extends Component {

    constructor(props) {
        super(props)
        this.state = {
            meeting: {
                meetingName: '',
                media: {
                    title: '',
                    type: '',
                    idTMDB: '',
                    posterPic: ''
                },
                date: '',
                description: '',
                seats: '',
                freeSeats: '',
                creator: '',
                location: {
                    type: '',
                    coordinates: []
                },
                snackList: []
            },
            results: [],
            resultVisibility: 'none',
            mouseIsOver: 'false',
            searchInput: ''
        }
        this.mediaService = new MediaService()
        this.meetingService = new MeetingService()
    }

    handleChange = e => {
        const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value
        this.setState({
            [e.target.name]: value
        })
    }

    handleResultsVisibility = visibility => {
        this.setState({ ...this.state, resultVisibility: visibility })
    }

    handleMouseOver = status => {
        this.setState({ ...this.state, mouseIsOver: status })
    }

    setMedia = media => {

        let mediaAux = {
            title: '',
            type: media.media_type,
            idTMDB: media.id,
            posterPic: media.poster_path
        }
        mediaAux.type === 'tv' ? mediaAux.title = media.name : mediaAux.title = media.title

        let meetingCopy = { ...this.state.meeting }
        meetingCopy.media = mediaAux
        this.setState({ meeting: meetingCopy, resultVisibility: 'none', searchInput: mediaAux.title })
    }

    handleSearch = e => {
        const value = e.target.value
        if (value.length > 0) {
            this.handleResultsVisibility('block')
            this.mediaService.getAll(value)
                .then(response => {
                    this.setState({ ...this.state, searchInput: value, results: response.data.results.filter(elm => (elm.media_type === 'tv' || elm.media_type === 'movie')) })
                })
                .catch(err => console.log(err))
        } else {
            this.handleResultsVisibility('none')
            this.setState({ ...this.state, searchInput: value, results: [] })
        }
    }

    handleFormSubmit = e => {
        e.preventDefault()
    }

    render() {
        return (
            <Container>

                <h1>Nueva montaña rusa</h1>
                <hr></hr>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group controlId="media-title"
                        onBlur={() => !this.state.mouseIsOver && this.handleResultsVisibility('none')}
                        onFocus={() => this.handleResultsVisibility('block')}>
                        <Form.Label>¿Qué vais a ver?</Form.Label>
                        <Form.Control name="media-title" type="text"

                            onChange={this.handleSearch}
                            autoComplete="off" />

                        <ul onMouseOver={() => this.handleMouseOver(true)} onMouseOut={() => this.handleMouseOver(false)} className='search-results' style={{ display: this.state.resultVisibility }}>
                            {this.state.results.map((elm, idx) => <li key={idx} onClick={e => this.setMedia(elm)}>
                                {elm.poster_path && <img className='miniature' src={`http://image.tmdb.org/t/p/w92${elm.poster_path}`} alt='poster' />}
                                <p>{elm.media_type === 'tv' ? elm.name : elm.title}</p>
                            </li>)}
                        </ul>

                    </Form.Group>

                    <Form.Group controlId="desc">
                        <Form.Label>Descripcion</Form.Label>
                        <Form.Control name="description" type="text" value={this.state.description} onChange={this.handleInputChange} />
                    </Form.Group>
                    <Form.Group controlId="inv">
                        <Form.Label>Inversiones</Form.Label>
                        <Form.Control name="inversions" type="text" value={this.state.inversions} onChange={this.handleInputChange} />
                    </Form.Group>
                    <Form.Group controlId="len">
                        <Form.Label>Longitud</Form.Label>
                        <Form.Control name="length" type="text" value={this.state.length} onChange={this.handleInputChange} />
                    </Form.Group>

                    <Button variant="dark" onClick={() => this.props.closeModal()} style={{ marginRight: '10px' }}>Cerrar</Button>
                    <Button variant="dark" type="submit">Crear montaña rusa</Button>
                </Form>
            </Container >
        )
    }

}

export default CreateMeetingForm
