import React, { Component } from 'react'
import { Switch, Route, Redirect } from 'react-router-dom'

import 'bootstrap/dist/css/bootstrap.min.css'

import './App.css'

// import AuthService from './../service/auth.service'
import Topbar from './ui/topbar/Topbar'
import Landing from './pages/landing/Landing'
import Signup from './pages/signup/Signup'
import Login from './pages/login/'

import AuthService from './../service/auth.service'


class App extends Component {

  constructor() {
    super()
    this.state = { loggedInUser: null }
    this.authService = new AuthService()
  }

  setTheUser = userObj => this.setState({ loggedInUser: userObj }, () => console.log('El estado de App ha cambiado:', this.state))

  fetchUser = () => {
    if (this.state.loggedInUser === null) {
      this.authService.isLoggedIn()
        .then(response => this.setTheUser(response.data))
        .catch(() => this.setTheUser(false))
    }
  }

  render() {

    this.fetchUser()

    return (
      <>
        <Topbar />

        <main>

          <Switch>
            <Route path="/" exact render={props => <Landing {...props} setTheUser={this.setTheUser} />} />
            <Route path="/signup" render={props => <Signup {...props} setTheUser={this.setTheUser} />} />
            <Route path="/login" render={props => <Login {...props} setTheUser={this.setTheUser} />} />
          </Switch>

        </main>

      </>
    )
  }
}

export default App
