import React, { Component } from 'react'

import Carousel, { Dots } from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';

import FollowingCard from './FollowingCard'
import MediaService from './../../../service/media.service'

import './Following.css'

class Following extends Component {

    constructor(props) {
        super(props)
        this.state = {
            tv: [],
            movie: []
        }
        this.MediaService = new MediaService()
    }

    initFollowingLists = () => { }

    componentDidMount = () => {
        const tvArr = [], movieArr = []
        this.props.user && this.props.user.followingList.forEach(elm => elm.type === 'tv' ? tvArr.push(elm) : movieArr.push(elm))

        this.setState({ tv: tvArr, movie: movieArr })
        // this.MediaService.getAll('de las flores')
        //     .then(response => console.log(response.data.results[0]))
        //     .catch(err => console.log(err))
    }

    render() {
        return (
            <>
                <h1>Listas de tus favoritos</h1>
                <section className='following'>
                    <h2>Series que sigues</h2>
                    <Carousel
                        slidesPerPage={3}
                        slidesPerScroll={2}
                        arrows
                        clickToChange
                        centered
                    >
                        {this.state.tv.map(elm => <FollowingCard key={elm._id} {...elm} />)}

                    </Carousel>
                </section>


                <h2>Pel√≠culas que sigues</h2>
            </>

        )
    }

}

export default Following
