import React, { Component } from 'react'

import MediaService from './../../../../service/media.service'
import MeetingService from './../../../../service/meeting.service'

import './CreateMeetingForm.css'

import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

class CreateMeetingForm extends Component {

    constructor() {
        super()
        this.state = {
            meetingName: '',
            media: '',
            results: []
        }
        this.mediaService = new MediaService()
        this.meetingService = new MeetingService()
    }

    handleChange = e => {
        const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value
        this.setState({
            [e.target.name]: value
        })
    }

    handleSearch = e => {
        const value = e.target.value
        if (value.length > 0) {
            this.mediaService.getAll(value)
                .then(response => {
                    console.log(value, '             ', response.data.results)
                    this.setState({ ...this.state, results: response.data.results.filter(elm => elm.media_type === 'tv' || elm.media_type === 'movie') })
                })
                .catch(err => console.log(err))
        } else {
            this.setState({ ...this.state, results: [] })
        }
    }

    handleFormSubmit = e => {
        e.preventDefault()
    }

    render() {
        return (
            <Container>

                <h1>Nueva montaña rusa</h1>
                <hr></hr>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group controlId="title">
                        <Form.Label>¿Qué vais a ver?</Form.Label>
                        <Form.Control name="media" type="text" value={this.state.title} onChange={this.handleSearch} autoComplete="off" />
                        <ul className='search-results' style={this.state.results.length > 0 ? { display: 'block' } : { display: 'none' }}>
                            {this.state.results.map((elm, idx) => <li key={idx}>{elm.media_type === 'tv' ? elm.name : elm.title}</li>)}
                        </ul>
                    </Form.Group>
                    <Form.Group controlId="desc">
                        <Form.Label>Descripcion</Form.Label>
                        <Form.Control name="description" type="text" value={this.state.description} onChange={this.handleInputChange} />
                    </Form.Group>
                    <Form.Group controlId="inv">
                        <Form.Label>Inversiones</Form.Label>
                        <Form.Control name="inversions" type="text" value={this.state.inversions} onChange={this.handleInputChange} />
                    </Form.Group>
                    <Form.Group controlId="len">
                        <Form.Label>Longitud</Form.Label>
                        <Form.Control name="length" type="text" value={this.state.length} onChange={this.handleInputChange} />
                    </Form.Group>

                    <Button variant="dark" onClick={() => this.props.closeModal()} style={{ marginRight: '10px' }}>Cerrar</Button>
                    <Button variant="dark" type="submit">Crear montaña rusa</Button>
                </Form>
            </Container>
            // <section className='meeting-creation'>
            //     <form onSubmit={this.handleFormSubmit}>
            //         <label>
            //             Título:
            //             <input name="media" value={this.state.title} onChange={this.handleSearch} type="text" />
            //             <ul className='search-results' style={this.state.results.length > 0 ? { display: 'block' } : { display: 'none' }}>
            //                 {this.state.results.map((elm, idx) => <li key={idx}>{elm.media_type === 'tv' ? elm.name : elm.title}</li>)}
            //             </ul>
            //         </label>
            //     </form>
            //     <input type="submit" value="Crear nueva película" />
            // </section>
        )
    }

}

export default CreateMeetingForm
