import React, { Component } from 'react'

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import meetingService from './../../../service/meeting.service'
import './MeetingDetails.css'

class MeetingDetails extends Component {

    constructor(props) {
        super(props)
        this.state = {
            meeting: null
        }
        this.meetingService = new meetingService()
    }

    getMeeting = id => {
        this.meetingService.getById(id)
            .then(response => this.setState({ meeting: response.data }))
            .catch(err => console.log(err))
    }

    componentDidMount = () => {
        this.getMeeting(this.props.match.params.id)

    }
    render() {
        console.log('los props', this.props)

        return (
            <Container fluid className="meeting-details">
                {this.state.meeting &&
                    <Row as='section' >
                        <Col md={{ span: 2, offset: 0 }}>
                            <img src={`http://image.tmdb.org/t/p/w185${this.state.meeting.media.posterPic}`} alt={this.state.meeting.media.title} />
                        </Col>
                        <Col md={{ span: 9, offset: 1 }}>
                            <h2>{this.state.meeting.media.title}</h2>
                            <h1>{this.state.meeting.meetingName}</h1>
                        </Col>
                        <Col md={{ span: 2, offset: 0 }}>
                            <img src={this.state.meeting.creator.profilePic} />
                        </Col>
                        <Col md={{ span: 4, offset: 1 }}>
                            <p>{this.state.meeting.description}</p>
                        </Col>
                        <Col md={{ span: 4, offset: 0 }}>
                            <p>{this.state.meeting.description}</p>
                        </Col>
                    </Row>}
            </Container>
        )
    }

}

export default MeetingDetails
