import './Topbar.css'

class Topbar extends Component {

    constructor(props) {
        super(props)
        this.state = {
        }
    }

    handleModal = visible => this.setState({ loginModal: visible })

    render() {

        return (
            <>
                <Navbar className="top-bar" fixed="top" expand="md">
                    <Navbar.Brand as="div"><Link to="/">the<span className="bold-weight">Watch</span>men</Link></Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="ml-auto">
                            <Nav.Link as="div" className="ml-auto"><button className="login-button" onClick={() => this.handleModal(true)}>Iniciar sesi√≥n</button></Nav.Link>
