import React, { Component } from 'react'
import MeetingService from './../../../service/meeting.service'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import './'

class Home extends Component {

    constructor() {
        super()
        this.state = {
            myMeetings: [],
            favouriteMeetings: [],
            nearMeetings: []
        }
        this.meetingService = new MeetingService()
    }

    getByTitle = (title) => {
        this.meetingService.getByTitle(title)
            .then(response => this.setState({ favouriteMeetings: response.data }))
            .catch(err => console.log(err))
    }

    getMeetings = () => {
        this.meetingService.getAllMeetings()
            .then(response => this.setState({ meetings: response.data }))
            .catch(err => console.log(err))
    }

    componentDidMount = () => {
        this.getMeetings()
        console.log(this.props.user)
    }

    render() {
        return (
            <Container fluid>
                <Row as='section'>
                    <Col className="search-card" as='article'>

                    </Col>
                </Row>
            </Container>
        )
    }

}

export default Home
