import React, { Component } from 'react'
import { Link } from 'react-router-dom'

import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'

import './Topbar.css'

class Topbar extends Component {

    constructor(props) {
        super(props)
        this.state = {
            searchValue:''
        }
    }


    render() {

        return (
            <>
                <Navbar className="top-bar" fixed="top" expand="md">
                    <Navbar.Brand as="div"><Link to="/">the<span className="bold-weight">Watch</span>men</Link></Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    {!this.props.user
                        ? <Nav className="ml-auto">
                            <Nav.Link as="div" className="ml-auto"><Link to="/login" >Iniciar Sesion</Link></Nav.Link>
                            <Nav.Link as="div" className="ml-auto"><Link to="/signup" >Registro</Link></Nav.Link>
                        </Nav>
                        : <Nav className="ml-auto">
                            <Nav.Link as="div" className="ml-auto"><Link to="/login" >Iniciar Sesion</Link></Nav.Link>
                            <Nav.Link as="div" className="ml-auto"><Link to="/signup" >Registro</Link></Nav.Link>
                        </Nav>
                    }
                </Navbar>

            </>
        )
    }

}

export default Topbar
